mapper.select:
SELECT

FROM attendance a
WHERE a.EMP_ID = #{empId}

    AND a.ATN_STATUS = #{status}

ORDER BY a.ATN_DATE DESC, a.ATN_IN DESC

SELECT COUNT(*)
FROM attendance a
WHERE a.EMP_ID = #{empId}

    AND a.ATN_STATUS = #{status}

SELECT

FROM attendance a
WHERE a.EMP_ID = #{empId, jdbcType=VARCHAR}
AND a.ATN_DATE = #{date, jdbcType=DATE}

SELECT

FROM attendance a
WHERE a.EMP_ID = #{empId}
AND a.ATN_DATE BETWEEN #{startDate} AND #{endDate}
